import React, { useState, useEffect } from 'react';
import { fetchPokemonDetails } from '../services/pokeapi';

const Pokemon = ({ pokemonUrl }) => {
  const [details, setDetails] = useState(null);

  useEffect(() => {
    fetchPokemonDetails(pokemonUrl).then(data => setDetails(data));
  }, [pokemonUrl]);

  if (!details) {
    return <div>Loading...</div>;
  }

  return (
    <div className="bg-white rounded-lg shadow-md p-4 mt-6">
      <div className="text-center">
        <h2 className="text-lg font-bold mb-2">
          {details.name.charAt(0).toUpperCase() + details.name.slice(1)}
        </h2>
        <img 
          src={details.imageUrl} 
          alt={details.name} 
          className="mx-auto w-32 h-32 object-contain mb-4"
        />

        <div className="text-left">
          <p><strong>Typen:</strong> {details.types.map(type => type.type.name).join(', ')}</p>
          <p><strong>Höhe:</strong> {details.height / 10} m</p>
          <p><strong>Gewicht:</strong> {details.weight / 10} kg</p>
          <p><strong>Grundlegende Fähigkeiten:</strong> {details.abilities.map(ability => ability.ability.name).join(', ')}</p>
          <p><strong>Base Experience:</strong> {details.base_experience}</p>
          <p><strong>Statistiken:</strong></p>
          <ul>
            {details.stats.map(stat => (
              <li key={stat.stat.name}>
                {stat.stat.name.toUpperCase()}: {stat.base_stat}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </div>
  );
};

export default Pokemon;
