import React, { useState, useEffect } from 'react';
import { fetchPokemonDetails } from '../services/pokeapi';
import { motion } from 'framer-motion';

const Pokemon = ({ pokemonUrl }) => {
  const [details, setDetails] = useState(null);

  useEffect(() => {
    fetchPokemonDetails(pokemonUrl).then(data => setDetails(data));
  }, [pokemonUrl]);

  if (!details) {
    return <div className="text-center p-4">Lädt...</div>;
  }

  return (
    <motion.div
      initial={{ opacity: 0, x: -100 }}
      animate={{ opacity: 1, x: 0 }}
      exit={{ opacity: 0, x: 100 }}
      transition={{ type: "spring", stiffness: 100 }}
      className="max-w-md mx-auto bg-white rounded-lg shadow-md overflow-hidden my-4"
    >
    <div className="min-h-screen bg-gray-100 text-gray-800">
      <div className="container mx-auto p-4">
        <h1 className="text-2xl font-bold text-center text-purple-700 mb-6">Pokémon Explorer</h1>
        <div className="flex">
          <div className="w-1/2">
            {selectedPokemonUrl && <Pokemon pokemonUrl={selectedPokemonUrl} />}
          </div>
          <div className="w-1/2">
            <PokemonList onPokemonSelect={setSelectedPokemonUrl} />
          </div>
        </div>
      </div>
    </div>
 </motion.div>
 );
};

export default Pokemon;
