import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';

import { fetchPokemonList } from '../services/pokeapi';
import Pokemon from './Pokemon';
import SearchBar from './SearchBar';

const PokemonList = () => {
  const [pokemonList, setPokemonList] = useState([]);
  const [filteredPokemonList, setFilteredPokemonList] = useState([]);
  const [selectedPokemonUrl, setSelectedPokemonUrl] = useState(null);
  const { i18n } = useTranslation();

  useEffect(() => {
    fetchPokemonList().then(pokemon => {
      setPokemonList(pokemon);
      setFilteredPokemonList(pokemon);
    });
  }, [i18n.language]);
  const handleSearch = (searchTerm) => {
    const filtered = pokemonList.filter(pokemon =>
      pokemon.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredPokemonList(filtered);
  };

  const handlePokemonClick = (url) => {
    setSelectedPokemonUrl(url); // Setzen der ausgewählten Pokémon-URL
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-4 py-4">
        {filteredPokemonList.map(pokemon => (
          <div 
            key={pokemon.names.en} 
            onClick={() => setSelectedPokemonUrl(pokemon.url)}
            className={`bg-white rounded-lg shadow-md p-4 hover:shadow-lg cursor-pointer transition duration-300 flex items-center space-x-4 ${selectedPokemonUrl === pokemon.url ? 'bg-blue-100' : ''}`}
          >
            <img src={pokemon.imageUrl} alt={pokemon.names.en} className="w-12 h-12 object-contain"/>
            <span className="capitalize">{pokemon.names[i18n.language] || pokemon.names.en}</span>
          </div>
        ))}
      </div>
      {selectedPokemonUrl && <Pokemon pokemonUrl={selectedPokemonUrl} />}
    </div>
  );
};

export default PokemonList;
