import React, { useState } from 'react';
import PokemonList from './components/PokemonList';
import Pokemon from './components/Pokemon';
import './App.css';

const App = () => {
  const [selectedPokemonUrl, setSelectedPokemonUrl] = useState(null);

  return (
    <div className="min-h-screen bg-gray-100 text-gray-800">
      <div className="container mx-auto p-4">
        <h1 className="text-2xl font-bold text-center text-purple-700 mb-6">PokéDex</h1>
        <div className="flex">
          <div className="w-1/2 h-1/2">
            {selectedPokemonUrl ? (
              <Pokemon pokemonUrl={selectedPokemonUrl} />
            ) : (
              <div className="text-center p-4">Wähle ein Pokémon, um mehr zu erfahren.</div>
            )}
          </div>
          <div className="w-1/2">
            <PokemonList onPokemonSelect={setSelectedPokemonUrl} />
          </div>
        </div>
      </div>
    </div>
  );
};

export default App;
