import React, { useState, useEffect } from 'react';
import { useTranslation } from 'react-i18next';
import { fetchPokemonDetails } from '../services/pokeapi';

const Pokemon = ({ pokemonUrl }) => {
  const [details, setDetails] = useState(null);
  const { t, i18n } = useTranslation();

  useEffect(() => {
    fetchPokemonDetails(pokemonUrl).then(data => setDetails(data));
  }, [pokemonUrl, i18n.language]);

  if (!details) {
    return <div>{t('loading')}</div>;
  }

  const pokemonName = details.names[i18n.language] || details.names.en;

  return (
    <div className="max-w-md mx-auto bg-white rounded-lg shadow-md overflow-hidden my-4">
      <div className="p-4">
        <div className="text-center mb-4">
          <img 
            src={details.imageUrl} 
            alt={pokemonName} 
            className="mx-auto h-40 w-40 object-contain"
          />
          <h2 className="text-xl font-semibold text-gray-800 capitalize">{pokemonName}</h2>
        </div>

        <div className="text-gray-700">
          <div className="mb-2">
            <h3 className="text-lg font-medium">{t('Details')}</h3>
            <p><strong>{t('Types')}:</strong> {details.types.map(type => type.type.name).join(', ')}</p>
            <p><strong>{t('Height')}:</strong> {details.height / 10} m</p>
            <p><strong>{t('Weight')}:</strong> {details.weight / 10} kg</p>
            <p><strong>{t('Basic Abilities')}:</strong> {details.abilities.map(ability => ability.ability.name).join(', ')}</p>
            <p><strong>{t('Base Experience')}:</strong> {details.base_experience}</p>
          </div>

          <div>
            <h3 className="text-lg font-medium">{t('Statistics')}</h3>
            <ul className="list-disc pl-5">
              {details.stats.map(stat => (
                <li key={stat.stat.name} className="capitalize">
                  {stat.stat.name}: {stat.base_stat}
                </li>
              ))}
            </ul>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Pokemon;
