import React, { useState, useEffect } from 'react';
import { fetchPokemonList } from '../services/pokeapi';
import Pokemon from './Pokemon';
import SearchBar from './SearchBar';

const PokemonList = () => {
  const [pokemonList, setPokemonList] = useState([]);
  const [filteredPokemonList, setFilteredPokemonList] = useState([]);
  const [selectedPokemon, setSelectedPokemon] = useState(null);

  useEffect(() => {
    fetchPokemonList().then(pokemon => {
      setPokemonList(pokemon);
      setFilteredPokemonList(pokemon);
    });
  }, []);

  const handleSearch = (searchTerm) => {
    const filtered = pokemonList.filter(pokemon =>
      pokemon.name.toLowerCase().includes(searchTerm.toLowerCase())
    );
    setFilteredPokemonList(filtered);
  };

  return (
    <div>
      <SearchBar onSearch={handleSearch} />
      <div className="grid grid-cols-2 sm:grid-cols-3 lg:grid-cols-4 gap-4">
        {filteredPokemonList.map(pokemon => (
          <div 
            key={pokemon.name} 
            onClick={() => setSelectedPokemon(pokemon.url)}
            className="bg-white rounded-lg shadow-md p-4 hover:shadow-lg cursor-pointer transition duration-300 flex items-center space-x-4"
          >
            <img src={pokemon.imageUrl} alt={pokemon.name} className="w-12 h-12 object-contain"/>
            <span className="capitalize">{pokemon.name}</span>
          </div>
        ))}
      </div>
      {selectedPokemon && <Pokemon pokemonUrl={selectedPokemon} />}
    </div>
  );
};

export default PokemonList;
