import React from 'react';

const BorderedText = ({ text }) => {
  const style = {
    border: '1mm solid black',
    borderRadius: '10px',
    backgroundColor: '#f2f2f2', // leicht grauer Hintergrund
    padding: '5px',
    maxWidth: '300px', // maximale Breite des Containers
    textAlign: 'center'
  };

  return (
    <div style={style}>
      {text}
    </div>
  );
};

export default BorderedText;
