import React, { useState, useEffect } from 'react';
import { fetchPokemonDetails } from '../services/pokeapi';

const Pokemon = ({ pokemonUrl }) => {
  const [details, setDetails] = useState(null);

  useEffect(() => {
    fetchPokemonDetails(pokemonUrl).then(data => setDetails(data));
  }, [pokemonUrl]);

  return (
    <div className="bg-white rounded-lg shadow-md p-4 mt-6">
      {details && (
        <div className="text-center">
          <h2 className="text-lg font-bold">{details.name.charAt(0).toUpperCase() + details.name.slice(1)}</h2>
          <img src={details.imageUrl} alt={details.name} className="mx-auto w-32 h-32 object-contain" />
          {/* Weitere Details hier anzeigen */}
        </div>
      )}
    </div>
  );
};

export default Pokemon;
