export const fetchPokemonList = async () => {
  const response = await fetch('https://pokeapi.co/api/v2/pokemon/?limit=1200');
  const data = await response.json();
  const pokemonDetails = await Promise.all(
    data.results.map(async (pokemon) => {
      const pokemonResponse = await fetch(pokemon.url);
      const pokemonData = await pokemonResponse.json();

      // Abfrage der Pokémon-Art für den deutschen Namen
      const speciesResponse = await fetch(pokemonData.species.url);
      const speciesData = await speciesResponse.json();
      const germanName = speciesData.names.find(name => name.language.name === 'de').name;

      return {
        name: germanName,
        url: pokemon.url,
        imageUrl: pokemonData.sprites.front_default,
      };
    })
  );
  return pokemonDetails;
};

export const fetchPokemonDetails = async (url) => {
  const response = await fetch(url);
  const data = await response.json();

  // URL für das offizielle Artwork
  const artworkUrl = data.sprites.other['official-artwork'].front_default;

  // Typen in deutscher Sprache
  const typesInGerman = await Promise.all(
    data.types.map(async (type) => {
      const typeResponse = await fetch(type.type.url);
      const typeData = await typeResponse.json();
      const germanType = typeData.names.find(name => name.language.name === 'de').name;
      return germanType;
    })
  );

  // Fähigkeiten in deutscher Sprache
  const abilitiesInGerman = await Promise.all(
    data.abilities.map(async (ability) => {
      const abilityResponse = await fetch(ability.ability.url);
      const abilityData = await abilityResponse.json();
      const germanAbility = abilityData.names.find(name => name.language.name === 'de').name;
      return germanAbility;
    })
  );

  // Umrechnung von Größe und Gewicht
  const heightInMeters = data.height / 10;
  const weightInKilograms = data.weight / 10;

  return {
    ...data,
    name: data.name, // Der deutsche Name wird bereits in der Liste gesetzt
    imageUrl: artworkUrl,
    types: typesInGerman,
    abilities: abilitiesInGerman,
    height: heightInMeters,
    weight: weightInKilograms,
    base_experience: data.base_experience,
    stats: data.stats.map(stat => ({ name: stat.stat.name, value: stat.base_stat }))
  };
};
