export const fetchPokemonList = async () => {
  const response = await fetch('https://pokeapi.co/api/v2/pokemon/?limit=151');
  const data = await response.json();
  const pokemonDetails = await Promise.all(
    data.results.map(async (pokemon) => {
      const pokemonResponse = await fetch(pokemon.url);
      const pokemonData = await pokemonResponse.json();

      // Abfrage der Pokémon-Art für den deutschen Namen
      const speciesResponse = await fetch(pokemonData.species.url);
      const speciesData = await speciesResponse.json();
      const germanName = speciesData.names.find(name => name.language.name === 'de').name;

      return {
        name: germanName,
        url: pokemon.url,
        imageUrl: pokemonData.sprites.front_default,
      };
    })
  );
  return pokemonDetails;
};

export const fetchPokemonDetails = async (url) => {
    const response = await fetch(url);
    const data = await response.json();
  
    // Abfrage der Pokémon-Art für den deutschen Namen und Beschreibung
    const speciesResponse = await fetch(data.species.url);
    const speciesData = await speciesResponse.json();
    const germanName = speciesData.names.find(name => name.language.name === 'de').name;
    const germanDescription = speciesData.flavor_text_entries.find(entry => entry.language.name === 'de').flavor_text.replace(/(\r\n|\n|\r|\f)/gm, " ");
  
    // Fähigkeiten auf Deutsch abrufen
    const abilities = await Promise.all(
      data.abilities.map(async (ability) => {
        const abilityResponse = await fetch(ability.ability.url);
        const abilityData = await abilityResponse.json();
        const germanAbilityName = abilityData.names.find(name => name.language.name === 'de').name;
        return germanAbilityName;
      })
    );
  
    // Typen auf Deutsch abrufen
    const types = data.types.map((type) => type.type.name); // Direkter Zugriff auf den Namen, da Typen klein sind und keine große Übersetzung benötigen
  
    const imageUrl = data.sprites.front_default;
  
    return { 
      ...data, 
      name: germanName, 
      description: germanDescription,
      abilities,
      types,
      imageUrl 
    };
  };
